import React from 'react'

const Sidebar = (props) => {

  const noteElement = props.notes.map((note)=><li>{note.id}</li>)
  return (
    <div className="sidebar-header">
      <h2>Notes</h2><span style={{color:" blue"}}><i class="fa-solid fa-plus fa-xl"  ></i></span>
      <div>{noteElement}</div>
    </div>
  )
}

export default Sidebar
