import React from 'react'

const Sidebar = (props) => {
  return (
    <div className="sidebar-header">
      <h2>Notes</h2><span style={{color:" blue"}}><i class="fa-solid fa-plus fa-xl"  ></i></span>
      <div>{props.notes.map((note,index)=><li>{index}</li>)}</div>
    </div>
  )
}

export default Sidebar
