  return (
    <div className="container"> 
    <div className="header"><Header/></div>
     <div className="sidebar"><Sidebar notes={notes} setCurrentNoteId={setCurrentNoteId} createNewNote={createNewNote}/></div>
     <div className="content"> {notes.length===0 ? <button onClick={createNewNote}>Create New Note</button> :<Content getCurrentNote={getCurrentNote()}/> } </div> 
     <div className="footer"><Footer/></div>
     </div>

  );
}

export default App;
