import React from 'react'

const Content = (props) => {
  return (
    <div>
    <p>{props.getCurrentNote.title}</p>
    
    <textarea className="noteContent"
        name="noteContent"
        value={props.getCurrentNote.text}
        onChange={props.updateNote}
      />
    </div>
  )
}

export default Content
