import React from 'react'

const Sidebar = (props) => {

  const noteElements = props.notes.map((note, index) => (
    <div  key={note.id} >  
    <div className="noteElement" onClick={() => props.setCurrentNoteId(note.id)}>
            <h4>Note {index + 1}</h4> <span style={{color:"black"}}><i class="fa-solid fa-xmark-large"></i></span>
      </div>
    </div>
   
))

  return (
    <div>
    <div className="sidebar-header">
      <h2>Notes</h2><span style={{color:"darkgreen"}}><i class="fa-solid fa-plus fa-xl"  onClick={props.createNewNote}></i></span>
    </div>
    <div>{noteElements} </div>
    </div>
  )
}

export default Sidebar
